<script>
export default {
  computed: {
    hideSVG() {
      return () => {
        if (this.$route.name == "index" || this.$route.name == "finishPage") {
          return false;
        } else {
          return true;
        }
      };
    },
  },
  mounted() {
    // this.$router.push("/");
    this.$nextTick(() => {
      //禁用右鍵
      document.oncontextmenu = new Function("event.returnValue=false");
      // 禁用選擇
      document.onselectstart = new Function("event.returnValue=false");
    });
  },
};
</script>

